import React, { useState } from "react";
import { reserveSeat } from "./api";

export default function SeatMap({ seats }) {
  const [selectedSeat, setSelectedSeat] = useState(null);
  const [name, setName] = useState("");
  const [surname, setSurname] = useState("");

  const handleSeatClick = (seat) => {
    if (seat.is_booked) return;
    setSelectedSeat(seat);
    setName("");
    setSurname("");
  };

  const handleReserve = async () => {
    if (!name || !surname) {
      alert("Please enter your first and last name.");
      return;
    }

    await reserveSeat(selectedSeat.id, name, surname);
    alert(`Seat ${selectedSeat.seat_number} reserved`);
    window.location.reload();
  };

  return (
    <div className="plane-wrapper">
      <h2>Koltuk Seçimi</h2>

      {}
      <div className="cockpit">✈</div>

      {}
      <div className="plane-body">
        {seats.map((s) => (
          <div
            key={s.id}
            onClick={() => handleSeatClick(s)}
            className={`seat 
              ${s.is_booked ? "booked" : "free"} 
              ${selectedSeat?.id === s.id ? "selected" : ""}`}
          >
            {s.seat_number}
          </div>
        ))}
      </div>

      {}
      {selectedSeat && !selectedSeat.is_booked && (
        <div className="reservation-box">
          <h3>Seat: {selectedSeat.seat_number}</h3>
          <input
            placeholder="Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <input
            placeholder="Surname"
            value={surname}
            onChange={(e) => setSurname(e.target.value)}
          />
          <button onClick={handleReserve}>Rezerve Et</button>
        </div>
      )}
    </div>
  );
}
