import { useState } from "react";
import { fetchFlights, fetchSeats } from "./api";
import FlightList from "./FlightList";
import SeatMap from "./SeatMap";

export default function App() {
  const [flights, setFlights] = useState([]);
  const [seats, setSeats] = useState([]);

  const search = async () => {
    const data = await fetchFlights("IST", "AMS");
    setFlights(data);
    setSeats([]);
  };

  const selectFlight = async (flight) => {
    const data = await fetchSeats(flight.id);
    setSeats(data);
  };

  return (
    <div>
      <h1>✈️ Flight Search</h1>
      <button onClick={search}>Search Flights</button>
      <FlightList flights={flights} onSelect={selectFlight} />
      <SeatMap seats={seats} />
    </div>
  );
}
