const API_URL = "http://localhost:8000";

export const fetchFlights = async (from, to, date) => {
  let url = `${API_URL}/flights?from_city=${from}&to_city=${to}`;

  if (date) {
    url += `&date=${date}`;
  }

  const res = await fetch(url);
  return res.json();
};

export const fetchSeats = async (flightId) => {
  const res = await fetch(`${API_URL}/flights/${flightId}/seats`);
  return res.json();
};
