import { useState } from "react";
import { fetchFlights, fetchSeats } from "./api";
import FlightList from "./FlightList";
import SeatMap from "./SeatMap";
import "./App.css";

export default function App() {
  const [flights, setFlights] = useState([]);
  const [seats, setSeats] = useState([]);

  const search = async () => {
    const data = await fetchFlights("IST", "AMS");
    setFlights(data);
    setSeats([]);
  };

  const selectFlight = async (flight) => {
    const data = await fetchSeats(flight.aircraft_id);
    setSeats(data);
  };

  return (
    <div className="container">
      <h1>✈️ Flight Booking System</h1>

      <button className="search-btn" onClick={search}>
        Search Flights
      </button>

      <FlightList flights={flights} onSelect={selectFlight} />
      {seats.length > 0 && <SeatMap seats={seats} />}
    </div>
  );
}
