import { useState } from "react";
import { fetchFlights, fetchSeats } from "./api";
import FlightList from "./FlightList";
import SeatMap from "./SeatMap";
import "./App.css";

export default function App() {
  const [fromCity, setFromCity] = useState("IST");
  const [toCity, setToCity] = useState("AMS");
  const [date, setDate] = useState("");
  const [flights, setFlights] = useState([]);
  const [seats, setSeats] = useState([]);

  const search = async () => {
    const data = await fetchFlights(fromCity, toCity, date);
    setFlights(data);
    setSeats([]);
  };

  const selectFlight = async (flight) => {
    const data = await fetchSeats(flight.id);
    setSeats(data);
  };

  return (
    <div className="container">
      <h1>✈️ Flight Booking System</h1>

      <div className="search-box">
        <input
          placeholder="From City"
          value={fromCity}
          onChange={(e) => setFromCity(e.target.value)}
        />

        <input
          placeholder="To City"
          value={toCity}
          onChange={(e) => setToCity(e.target.value)}
        />

        <input
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
        />

        <button className="search-btn" onClick={search}>
          Search Flights
        </button>
      </div>

      <FlightList flights={flights} onSelect={selectFlight} />

      {seats.length > 0 && (
        <>
          <h2 className="seat-title">Seat Map</h2>
          <SeatMap seats={seats} />
        </>
      )}
    </div>
  );
}
